mapper.select:
SELECT
	
FROM ssm_product_Size t 
WHERE t.id = #{id,jdbcType=SMALLINT}

SELECT 
	
FROM ssm_product_Size t
WHERE t.code = #{code,jdbcType=VARCHAR}

SELECT , a.login_name FROM ssm_product_Size t LEFT JOIN ssm_admin a ON a.id = t.update_aid
t.code LIKE '%${search.code}%'
AND t.name = #{search.name,jdbcType=VARCHAR}
